// TypeScript declaration file for ChatbotPage.module.css
// This file provides type safety and autocompletion for CSS module class names.

declare const styles: {
  readonly "button": string;         // Style for the send button
  readonly "chatbox": string;        // Style for the chat area
  readonly "container": string;      // Style for the main chatbot container
  readonly "header": string;         // Style for the header row (user info, logout)
  readonly "input": string;          // Style for the chat input field
  readonly "inputRow": string;       // Style for the input row (input + button)
  readonly "logoutButton": string;   // Style for the logout button
  readonly "message": string;        // Base style for message bubbles
  readonly "messageBot": string;     // Style for bot message bubbles
  readonly "messageUser": string;    // Style for user message bubbles
  readonly "dots": string;           // Style for typing indicator dots
  readonly "charCount": string;      // Style for character counter
  readonly "fadeIn": string;         // Style for fade-in animation
}
export = styles
